import React, { useState } from "react";
import "./HamburgerMenu.css";

function HamburgerMenu({ sections, onSelect, user, onLogout }) {
  const [open, setOpen] = useState(false);

  return (
    <div>
      {/* Butoni Hamburger */}
      <button
        className={`hamburger-btn ${open ? "active" : ""}`}
        onClick={() => setOpen(!open)}
        aria-label="Menu"
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      {/* Overlay për sfondin */}
      <div
        className={`overlay ${open ? "active" : ""}`}
        onClick={() => setOpen(false)}
      ></div>

      {/* Drawer Menu */}
      <nav className={`drawer-menu ${open ? "active" : ""}`}>
        <div style={{display:'flex',flexDirection:'column',height:'100%'}}>
          <div style={{padding:'1rem 1.25rem',borderBottom:'1px solid #e2e8f0'}}>
            <div style={{fontSize:'0.75rem',color:'#64748b',textTransform:'uppercase',letterSpacing:'0.5px'}}>Llogaria</div>
            <div style={{fontWeight:600,color:'#1e293b'}}>{user?.username || '—'}</div>
            {user?.role === 'admin' && <div style={{fontSize:'0.65rem',color:'#fff',background:'#ef4444',display:'inline-block',padding:'2px 6px',borderRadius:'6px',marginTop:'4px'}}>ADMIN</div>}
          </div>
          <ul style={{flex:1,overflowY:'auto',margin:0,padding:'0.75rem 0'}}>
            {sections.map((s) => (
              <li key={s} style={{listStyle:'none'}}>
                <button
                  className="menu-item"
                  onClick={() => {
                    onSelect(s);
                    setOpen(false);
                  }}
                >
                  {s}
                </button>
              </li>
            ))}
          </ul>
          <div style={{padding:'0.75rem 1.25rem',borderTop:'1px solid #e2e8f0'}}>
            <button onClick={()=>{ onLogout?.(); setOpen(false); }} style={{width:'100%',background:'#334155',color:'#fff',border:'none',padding:'0.65rem 0.9rem',borderRadius:'8px',cursor:'pointer',fontSize:'0.8rem',fontWeight:500}}>Dil</button>
          </div>
        </div>
      </nav>
    </div>
  );
}

export default HamburgerMenu;
