import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ReportPanel.css';
import SearchInput from './SearchInput';
import NameInput from './NameInput';

function ReportPanel() {
  const year = new Date().getFullYear();
  const janFirst = `${year}-01-01`;
  const today = new Date().toISOString().slice(0, 10);
  const [start, setStart] = useState(janFirst);
  const [end, setEnd] = useState(today);
  const [report, setReport] = useState(null);
  const [search, setSearch] = useState('');
  const [sectorId, setSectorId] = useState('');
  const [sectors, setSectors] = useState([]);
  const [toast, setToast] = useState(null);

  const fetchReport = async () => {
    if (!start || !end) return;
    try {
      const res = await axios.get(`http://localhost:4000/api/report?start=${start}&end=${end}`);
      setReport(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  useEffect(() => {
    if (start && end) fetchReport();
  }, [start, end]);

  useEffect(() => {
    const fetchSectors = async () => {
      try {
        const res = await axios.get('http://localhost:4000/api/sectors');
        setSectors(res.data);
      } catch (err) {
        setToast('Gabim në lidhje me serverin!');
      }
    };
    fetchSectors();
  }, []);

  const filteredWorkers = report?.workers.filter(w => {
    const matchesSearch =
      w.first_name.toLowerCase().includes(search.toLowerCase()) ||
      w.last_name.toLowerCase().includes(search.toLowerCase()) ||
      w.position.toLowerCase().includes(search.toLowerCase());
    const matchesSector = sectorId === '' || String(w.sector_id) === String(sectorId);
    return matchesSearch && matchesSector;
  }) || [];

  return (
    <div className="report-panel">
      <h2 className="report-title">Raport i Përgjithshëm</h2>
      <div className="report-controls">
  <SearchInput type="date" name="start" value={start} onChange={e => setStart(e.target.value)} required />
  <SearchInput type="date" name="end" value={end} onChange={e => setEnd(e.target.value)} required />
        <button className="report-btn-main" onClick={fetchReport}>Shfaq Raportin</button>
        <select value={sectorId} onChange={e => setSectorId(e.target.value)} className="report-sector-dropdown">
          <option value="">Të gjitha sektorët</option>
          {sectors.map(sector => (
            <option key={sector.id} value={sector.id}>{sector.name}</option>
          ))}
        </select>
        <NameInput
          name="search"
          value={search}
          onChange={e => setSearch(e.target.value)}
          placeholder="Kërko punëtorin..."
        />
        {report && <button className="report-btn-print" onClick={() => window.print()}>Printo</button>}
      </div>
      {report && (
        <div className="report-table-wrapper">
          <table className="table report-table">
            <thead>
              <tr>
                <th>Punëtori</th>
                <th>Sektori</th>
                <th>Orët</th>
                <th>Paga</th>
                <th>Avancat</th>
                <th>Balanca</th>
              </tr>
            </thead>
            <tbody>
              {filteredWorkers.map(worker => {
                const wh = report.workhours.filter(w => w.worker_id === worker.id);
                const paga = report.payments.filter(p => p.worker_id === worker.id && p.type === 'paga');
                const avanc = report.payments.filter(p => p.worker_id === worker.id && p.type === 'avanc');
                const totalHours = wh.reduce((sum, w) => sum + w.hours, 0);
                const totalPaga = paga.reduce((sum, p) => sum + p.amount, 0);
                const totalAvanc = avanc.reduce((sum, p) => sum + p.amount, 0);
                const expected = totalHours * worker.hourly_rate;
                const balance = totalPaga + totalAvanc - expected;
                return (
                  <tr key={worker.id}>
                    <td>{worker.first_name} {worker.last_name}</td>
                    <td>{worker.sector_name || '-'}</td>
                    <td>{totalHours}</td>
                    <td>{totalPaga} €</td>
                    <td>{totalAvanc} €</td>
                    <td>{balance.toFixed(2)} €</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
      {toast && <div className="report-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default ReportPanel;
