import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WorkHoursPanel.css';

function WorkHoursPanel() {
  const [workers, setWorkers] = useState([]);
  const [workhours, setWorkhours] = useState([]);
  const today = new Date().toISOString().slice(0, 10);
  const [form, setForm] = useState({ worker_id: '', date: today, hours: '' });
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
    fetchWorkhours();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const fetchWorkhours = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workhours');
      setWorkhours(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (form.id) {
        await axios.put(`http://localhost:4000/api/workhours/${form.id}`, form);
        setToast('Orët u përditësuan!');
      } else {
        await axios.post('http://localhost:4000/api/workhours', form);
        setToast('Orët u regjistruan!');
      }
      setForm({ worker_id: '', date: today, hours: '' });
      fetchWorkhours();
    } catch (err) {
      setToast('Gabim gjatë ruajtjes!');
    }
  };

  const filteredWorkhours = workhours.filter(w => {
    const worker = workers.find(x => x.id === w.worker_id);
    return (
      !search ||
      (worker &&
        (worker.first_name + ' ' + worker.last_name)
          .toLowerCase()
          .includes(search.toLowerCase()))
    );
  });

  return (
    <div className="wh-panel">
      <h2 className="wh-title">Regjistrimi i Orëve të Punës</h2>
      <form className="wh-form" onSubmit={handleSubmit}>
        <select name="worker_id" value={form.worker_id} onChange={handleChange} required>
          <option value="">Zgjidh Punëtorin</option>
          {workers.map(worker => (
            <option key={worker.id} value={worker.id}>
              {worker.first_name} {worker.last_name} - {worker.position}
            </option>
          ))}
        </select>
        <input name="date" value={form.date} onChange={handleChange} type="date" required />
        <input name="hours" value={form.hours} onChange={handleChange} placeholder="Numri i orëve" type="number" step="0.01" required />
        <button type="submit" className="wh-btn-main">{form.id ? 'Ruaj Ndryshimet' : 'Shto Orët'}</button>
        {form.id && <button type="button" className="wh-btn-cancel" onClick={() => setForm({ worker_id: '', date: today, hours: '' })}>Anulo</button>}
      </form>
      <input
        type="text"
        className="wh-search"
        placeholder="Kërko punëtorin..."
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="wh-table-wrapper">
        <table className="table wh-table">
          <thead>
            <tr>
              <th>Punëtori</th>
              <th>Data</th>
              <th>Orët</th>
              <th>Veprime</th>
            </tr>
          </thead>
          <tbody>
            {filteredWorkhours.map(w => {
              const worker = workers.find(x => x.id === w.worker_id);
              return (
                <tr key={w.id}>
                  <td>{worker ? `${worker.first_name} ${worker.last_name}` : '-'}</td>
                  <td>{w.date}</td>
                  <td>{w.hours}</td>
                  <td>
                    <button className="wh-btn-edit" onClick={() => setForm({ worker_id: w.worker_id, date: w.date, hours: w.hours, id: w.id })}>Edito</button>
                    <button className="wh-btn-delete" onClick={async () => { await axios.delete(`http://localhost:4000/api/workhours/${w.id}`); fetchWorkhours(); }}>Fshij</button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {toast && <div className="wh-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default WorkHoursPanel;
