import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './WorkHoursPanel.css';

export default function WorkHoursPanel({ worker }) {
  const [rows, setRows] = useState([]);
  const [form, setForm] = useState({ work_date: '', hours: '', description: '' });

  const load = async () => {
    const { data } = await axios.get('/api/work-hours', { params: { worker_id: worker?.id || '' } });
    setRows(data);
  };
  useEffect(() => { if (worker) load(); }, [worker]);

  const submit = async e => {
    e.preventDefault();
    if (!worker) return;
    const payload = { ...form, worker_id: worker.id, hours: parseFloat(form.hours) };
    if (!payload.work_date || !payload.hours) return;
    await axios.post('/api/work-hours', payload);
    setForm({ work_date: '', hours: '', description: '' });
    load();
  };
  const remove = async id => {
    if (!window.confirm('Fshije?')) return;
    await axios.delete(`/api/work-hours/${id}`);
    load();
  };

  return (
    <div className="panel">
      <h2>Orët {worker ? `(${worker.first_name})` : ''}</h2>
      {!worker && <p>Zgjidh një punëtor.</p>}
      {worker && <>
        <form className="inline" onSubmit={submit}>
          <input type="date" value={form.work_date} onChange={e => setForm({ ...form, work_date: e.target.value })} />
          <input type="number" step="0.01" placeholder="Orë" value={form.hours} onChange={e => setForm({ ...form, hours: e.target.value })} style={{ width: 80 }} />
          <input placeholder="Përshkrim" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} style={{ flex: 1, minWidth: 160 }} />
          <button type="submit">Shto</button>
        </form>
        <div className="list">
          <table>
            <thead><tr><th>Data</th><th>Orë</th><th>Shënim</th><th></th></tr></thead>
            <tbody>
              {rows.map(r => (
                <tr key={r.id}>
                  <td>{r.work_date}</td>
                  <td>{r.hours}</td>
                  <td>{r.description}</td>
                  <td><button onClick={() => remove(r.id)}>X</button></td>
                </tr>
              ))}
              {!rows.length && <tr><td colSpan="4">Ska</td></tr>}
            </tbody>
          </table>
        </div>
      </>}
    </div>
  );
}
