import React, { useEffect, useState } from 'react';
import { withBase } from '../api';

// Simple admin panel to list and approve pending users
export default function AdminApprovalPanel() {
  const [pending, setPending] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const load = () => {
    setLoading(true); setError(null);
    fetch(withBase('/api/users/pending'), { credentials: 'include' })
      .then(r => r.ok ? r.json() : Promise.reject(r))
      .then(setPending)
      .catch(e => setError('Nuk u lexua lista'))
      .finally(()=> setLoading(false));
  };

  useEffect(() => { load(); }, []);

  const approve = (id) => {
    fetch(withBase(`/api/users/${id}/approve`), { method:'POST', credentials:'include' })
      .then(r => r.ok ? r.json() : Promise.reject(r))
      .then(()=> load())
      .catch(()=> setError('Gabim gjatë aprovimit'));
  };

  return (
    <div style={{padding:'1rem'}}>
      <h3>Përdoruesit në pritje</h3>
      {loading && <p>Duke lexuar...</p>}
      {error && <p style={{color:'red'}}>{error}</p>}
      {pending.length === 0 && !loading && <p>Asnjë përdorues në pritje.</p>}
      <ul style={{listStyle:'none', padding:0}}>
        {pending.map(u => (
          <li key={u.id} style={{display:'flex', alignItems:'center', marginBottom:'0.5rem', gap:'0.5rem'}}>
            <span>{u.display_name || u.email}</span>
            <button onClick={()=>approve(u.id)}>Aprovo</button>
          </li>
        ))}
      </ul>
      <button onClick={load} disabled={loading}>Rifresko</button>
    </div>
  );
}
