import React from 'react';
import WorkerPanel from './components/WorkerPanel';
import WorkHoursPanel from './components/WorkHoursPanel';
import PaymentsPanel from './components/PaymentsPanel';
import ReportPanel from './components/ReportPanel';
import IndividualReportPanel from './components/IndividualReportPanel';
import SectorPanel from './components/SectorPanel';
import HamburgerMenu from './components/HamburgerMenu';
import { translations } from './locales';

function App() {
  const [section, setSection] = React.useState('Punëtorët');
  const [lang, setLang] = React.useState('sq');
  const t = translations[lang];
  const sections = [
    t['sector'] || 'Sektoret',
    t['worker'] || 'Punëtorët',
    t['hours'] || 'Orët e Punës',
    t['salary'] || 'Pagat & Avancat',
    t['report'] || 'Raporti Përgjithshëm',
    t['individualReport'] || 'Raporti Individual'
  ];
  return (
    <div>
      <header>
        <nav style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <h1 style={{ margin: 0 }}>Enisi-MS</h1>
          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
            <button className="btn btn-outline" onClick={() => setLang(lang === 'sq' ? 'it' : 'sq')}>
              {lang === 'sq' ? 'IT' : 'SQ'}
            </button>
            <HamburgerMenu sections={sections} onSelect={setSection} />
          </div>
        </nav>
      </header>
      <main>
        {section === (t['sector'] || 'Sektoret') && <SectorPanel t={t} />}
        {section === (t['worker'] || 'Punëtorët') && <WorkerPanel t={t} />}
        {section === (t['hours'] || 'Orët e Punës') && <WorkHoursPanel t={t} />}
        {section === (t['salary'] || 'Pagat & Avancat') && <PaymentsPanel t={t} />}
        {section === (t['report'] || 'Raporti Përgjithshëm') && <ReportPanel t={t} />}
        {section === (t['individualReport'] || 'Raporti Individual') && <IndividualReportPanel t={t} />}
      </main>
    </div>
  );
}

export default App;
