import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { withBase } from '../api';
import './WorkerPanel.css';
import NameInput from './NameInput';

function WorkerPanel() {
  const [workers, setWorkers] = useState([]);
  const [sectors, setSectors] = useState([]);
  const [form, setForm] = useState({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' });
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
    fetchSectors();
  }, []);

  const fetchSectors = async () => {
    try {
  const res = await axios.get(withBase('/api/sectors'));
      setSectors(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const fetchWorkers = async () => {
    try {
  const res = await axios.get(withBase('/api/workers'));
      setWorkers(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editId) {
  await axios.put(withBase(`/api/workers/${editId}`), form);
        setToast('Punëtori u përditësua!');
      } else {
  await axios.post(withBase('/api/workers'), form);
        setToast('Punëtori u shtua!');
      }
      setForm({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' });
      setEditId(null);
      fetchWorkers();
    } catch (err) {
      setToast('Gabim gjatë ruajtjes!');
    }
  };

  const handleEdit = worker => {
    setForm({
      first_name: worker.first_name,
      last_name: worker.last_name,
      position: worker.position,
      hourly_rate: worker.hourly_rate,
      sector_id: worker.sector_id || ''
    });
    setEditId(worker.id);
  };

  const handleDelete = async id => {
    try {
  await axios.delete(withBase(`/api/workers/${id}`));
      setToast('Punëtori u fshi!');
      fetchWorkers();
    } catch (err) {
      setToast('Gabim gjatë fshirjes!');
    }
  };

  const filteredWorkers = workers.filter(w =>
    w.first_name.toLowerCase().includes(search.toLowerCase()) ||
    w.last_name.toLowerCase().includes(search.toLowerCase()) ||
    w.position.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="worker-panel">
      <h2 className="wp-title">Regjistrimi i Punëtorëve</h2>
      <form className="wp-form" onSubmit={handleSubmit}>
  <NameInput name="first_name" value={form.first_name} onChange={handleChange} placeholder="Emri" required />
  <NameInput name="last_name" value={form.last_name} onChange={handleChange} placeholder="Mbiemri" required />
        <label className="label">
          <span className="icon">
            <svg width="30" height="30" fill="none" viewBox="0 0 24 24">
              <path stroke="currentColor" strokeWidth="1.25" d="M7 17v1a1 1 0 0 0 1 1h8a1 1 0 0 0 1-1v-1a3 3 0 0 0-3-3h-4a3 3 0 0 0-3 3Zm8-9a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
            </svg>
          </span>
          <select name="sector_id" value={form.sector_id} onChange={handleChange} required className="input">
            <option value="">Zgjidh Sektorin</option>
            {sectors.map(sector => (
              <option key={sector.id} value={sector.id}>{sector.name}</option>
            ))}
          </select>
        </label>
  <NameInput name="hourly_rate" value={form.hourly_rate} onChange={handleChange} placeholder="Çmimi i orës" type="number" step="0.01" required />
        <button type="submit" className="wp-btn-main">{editId ? 'Ruaj Ndryshimet' : 'Shto Punëtorin'}</button>
        {editId && <button type="button" className="wp-btn-cancel" onClick={() => { setEditId(null); setForm({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' }); }}>Anulo</button>}
      </form>
      <NameInput
        name="search"
        value={search}
        onChange={e => setSearch(e.target.value)}
        placeholder="Kërko..."
      />
      <div className="wp-table-wrapper">
        <table className="table wp-table">
          <thead>
            <tr>
              <th>Emri</th>
              <th>Mbiemri</th>
              <th>Pozicioni</th>
              <th>Sektori</th>
              <th>Çmimi i Orës</th>
              <th>Veprime</th>
            </tr>
          </thead>
          <tbody>
            {filteredWorkers.map(worker => (
              <tr key={worker.id}>
                <td>{worker.first_name}</td>
                <td>{worker.last_name}</td>
                <td>{worker.position}</td>
                <td>{worker.sector_name || '-'}</td>
                <td>{worker.hourly_rate} €</td>
                <td>
                  <button className="wp-btn-edit" onClick={() => handleEdit(worker)}>Edito</button>
                  <button className="wp-btn-delete" onClick={() => handleDelete(worker.id)}>Fshij</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {toast && <div className="wp-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default WorkerPanel;
