import React, { useState } from 'react';

function HamburgerMenu({ sections, onSelect }) {
  const [open, setOpen] = useState(false);

  return (
    <>
      {/* Overlay */}
      <div
        className={open ? 'overlay active' : 'overlay'}
        onClick={() => setOpen(false)}
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0,0,0,0.5)',
          opacity: open ? 1 : 0,
          pointerEvents: open ? 'auto' : 'none',
          transition: '0.3s',
          zIndex: 998
        }}
      />
      {/* Header Hamburger Button */}
      <div style={{ position: 'relative', zIndex: 999 }}>
        <div
          className={open ? 'hamburger active' : 'hamburger'}
          onClick={() => setOpen(!open)}
          style={{
            width: '30px',
            height: '22px',
            cursor: 'pointer',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            transition: '0.3s ease',
          }}
          aria-label="Menu"
        >
          <span style={{
            height: '4px',
            width: '100%',
            background: '#fff',
            borderRadius: '2px',
            transition: '0.4s ease',
            transform: open ? 'rotate(45deg) translateY(9px)' : 'none',
            display: 'block'
          }} />
          <span style={{
            height: '4px',
            width: '100%',
            background: '#fff',
            borderRadius: '2px',
            transition: '0.4s ease',
            opacity: open ? 0 : 1,
            display: 'block'
          }} />
          <span style={{
            height: '4px',
            width: '100%',
            background: '#fff',
            borderRadius: '2px',
            transition: '0.4s ease',
            transform: open ? 'rotate(-45deg) translateY(-9px)' : 'none',
            display: 'block'
          }} />
        </div>
        {/* Menu Drawer */}
        <nav
          className={open ? 'menu active' : 'menu'}
          style={{
            position: 'fixed',
            top: 0,
            left: open ? 0 : '-280px',
            width: '280px',
            height: '100%',
            background: '#4b0000',
            paddingTop: '80px',
            transition: '0.4s ease',
            boxShadow: '2px 0 10px rgba(0,0,0,0.25)',
            zIndex: 999
          }}
        >
          <ul style={{ listStyle: 'none' }}>
            {sections.map((s, i) => (
              <li
                key={s}
                style={{
                  opacity: open ? 1 : 0,
                  transform: open ? 'translateX(0)' : 'translateX(-30px)',
                  transition: '0.4s ease',
                }}
              >
                <button
                  style={{
                    display: 'block',
                    color: '#fff',
                    background: 'none',
                    border: 'none',
                    padding: '15px 25px',
                    fontSize: '1rem',
                    width: '100%',
                    textAlign: 'left',
                    cursor: 'pointer',
                    transition: 'background 0.2s',
                  }}
                  onClick={() => { onSelect(s); setOpen(false); }}
                  onMouseOver={e => (e.target.style.background = 'rgba(255,255,255,0.1)')}
                  onMouseOut={e => (e.target.style.background = 'none')}
                >
                  {s}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </>
  );
}

export default HamburgerMenu;
