import React, { useState } from 'react';
import WorkerPanel from './components/WorkerPanel';
import WorkHoursPanel from './components/WorkHoursPanel';
import PaymentsPanel from './components/PaymentsPanel';
import IndividualReportPanel from './components/IndividualReportPanel';
import ReportPanel from './components/ReportPanel';
import Login from './components/Login';
import './app.css';

const tabs = [
  { key: 'workers', label: 'Punëtorët' },
  { key: 'hours', label: 'Orët' },
  { key: 'payments', label: 'Pagat/Avancat' },
  { key: 'reports', label: 'Raporte' }
];

export default function App() {
  const [active, setActive] = useState('workers');
  const [selectedWorker, setSelectedWorker] = useState(null);
  const [auth, setAuth] = useState(true); // vendos auth real më vonë

  if (!auth) return <Login onSuccess={()=>setAuth(true)} />;

  return (
    <div className="app">
      <header>
        <h1>Enisi</h1>
        <nav>
          {tabs.map(t=>(
            <button
              key={t.key}
              className={active===t.key?'active':''}
              onClick={()=>setActive(t.key)}
            >{t.label}</button>
          ))}
        </nav>
      </header>
      <main>
        {active==='workers' && <WorkerPanel onSelect={setSelectedWorker} selectedWorker={selectedWorker} />}
        {active==='hours' && <WorkHoursPanel worker={selectedWorker} />}
        {active==='payments' && <PaymentsPanel worker={selectedWorker} />}
        {active==='reports' && (
          <>
            <IndividualReportPanel worker={selectedWorker} />
            <ReportPanel />
          </>
        )}
      </main>
      <footer><small>&copy; {new Date().getFullYear()} Enisi</small></footer>
    </div>
  );
}
