import React, { useState } from "react";
import "./HamburgerMenu.css";

const orderedSections = [
  "Sektorët",
  "Punëtorët",
  "Orët e Punës",
  "Pagat & Avancat",
  "Raporti Përgjithshëm",
  "Raporti Individual",
];

function HamburgerMenu({ sections, onSelect }) {
  const [open, setOpen] = useState(false);
  // Radhit seksionet sipas listës së kërkuar
  const menuSections = orderedSections.filter((s) => sections.includes(s));

  return (
    <div>
      {/* Butoni Hamburger */}
      <button
        className={`hamburger-btn ${open ? "active" : ""}`}
        onClick={() => setOpen(!open)}
        aria-label="Menu"
      >
        <span></span>
        <span></span>
        <span></span>
      </button>

      {/* Overlay për sfondin */}
      <div
        className={`overlay ${open ? "active" : ""}`}
        onClick={() => setOpen(false)}
      ></div>

      {/* Drawer Menu */}
      <nav className={`drawer-menu ${open ? "active" : ""}`}>
        <ul>
          {menuSections.map((s) => (
            <li key={s}>
              <button
                className="menu-item"
                onClick={() => {
                  onSelect(s);
                  setOpen(false);
                }}
              >
                {s}
              </button>
            </li>
          ))}
        </ul>
      </nav>
    </div>
  );
}

export default HamburgerMenu;
