import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { withBase } from '../api';
import './SectorPanel.css';
import NameInput from './NameInput';

function SectorPanel() {
  const [sectors, setSectors] = useState([]);
  const [name, setName] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchSectors();
  }, []);

  const fetchSectors = async () => {
    try {
  const res = await axios.get(withBase('/api/sectors'));
      setSectors(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
  await axios.post(withBase('/api/sectors'), { name });
      setToast('Sektori u shtua!');
      setName('');
      fetchSectors();
    } catch (err) {
      setToast('Sektori ekziston!');
    }
  };

  const handleDelete = async id => {
    try {
  await axios.delete(withBase(`/api/sectors/${id}`));
      setToast('Sektori u fshi!');
      fetchSectors();
    } catch (err) {
      setToast('Gabim gjatë fshirjes!');
    }
  };

  return (
    <div className="sector-panel">
      <h2 className="sector-title">Regjistrimi i Sektorëve</h2>
      <form className="sector-form" onSubmit={handleSubmit}>
        <NameInput
          name="sector_name"
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="Emri i sektorit"
          required
        />
        <button type="submit" className="sector-btn-main">Shto Sektorin</button>
      </form>
      <div className="sector-table-wrapper">
        <table className="table sector-table">
          <thead>
            <tr>
              <th>Emri i Sektorit</th>
              <th>Veprime</th>
            </tr>
          </thead>
          <tbody>
            {sectors.map(sector => (
              <tr key={sector.id}>
                <td>{sector.name}</td>
                <td>
                  <button className="sector-btn-delete" onClick={() => handleDelete(sector.id)}>Fshij</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {toast && <div className="sector-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default SectorPanel;
