import React, { useState, useEffect } from 'react';
import axios from 'axios';

function PaymentsPanel() {
  const [workers, setWorkers] = useState([]);
  const [payments, setPayments] = useState([]);
  const [form, setForm] = useState({ worker_id: '', date: '', amount: '', type: 'paga' });
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchWorkers();
    fetchPayments();
  }, []);

  const fetchWorkers = async () => {
    const res = await axios.get('http://localhost:4000/api/workers');
    setWorkers(res.data);
  };

  const fetchPayments = async () => {
    const res = await axios.get('http://localhost:4000/api/payments');
    setPayments(res.data);
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    await axios.post('http://localhost:4000/api/payments', form);
    setForm({ worker_id: '', date: '', amount: '', type: 'paga' });
    fetchPayments();
  };

  const filteredPayments = payments.filter(p => {
    const worker = workers.find(x => x.id === p.worker_id);
    return (
      (!search || (worker && (worker.first_name + ' ' + worker.last_name).toLowerCase().includes(search.toLowerCase())))
    );
  });

  return (
    <div>
      <h2>Regjistrimi i Pagave dhe Avancave</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
        <select name="worker_id" value={form.worker_id} onChange={handleChange} required>
          <option value="">Zgjidh Punëtorin</option>
          {workers.map(worker => (
            <option key={worker.id} value={worker.id}>
              {worker.first_name} {worker.last_name} - {worker.position}
            </option>
          ))}
        </select>
        <input name="date" value={form.date} onChange={handleChange} type="date" required />
        <input name="amount" value={form.amount} onChange={handleChange} placeholder="Shuma" type="number" step="0.01" required />
        <select name="type" value={form.type} onChange={handleChange} required>
          <option value="paga">Paga</option>
          <option value="avanc">Avanc</option>
        </select>
        <button type="submit">Shto</button>
      </form>
      <input
        type="text"
        placeholder="Kërko punëtorin..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ margin: '1rem 0', padding: '0.5rem', width: '100%', maxWidth: '300px' }}
      />
      <table className="table">
        <thead>
          <tr>
            <th>Punëtori</th>
            <th>Data</th>
            <th>Shuma</th>
            <th>Lloji</th>
          </tr>
        </thead>
        <tbody>
          {filteredPayments.map(p => {
            const worker = workers.find(x => x.id === p.worker_id);
            return (
              <tr key={p.id}>
                <td>{worker ? `${worker.first_name} ${worker.last_name}` : '-'}</td>
                <td>{p.date}</td>
                <td>{p.amount} €</td>
                <td>{p.type === 'paga' ? 'Paga' : 'Avanc'}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default PaymentsPanel;
