import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './IndividualReportPanel.css';

export default function IndividualReportPanel({ worker }) {
  const [report,setReport]=useState(null);
  useEffect(()=>{
    if(worker){
      axios.get(`/api/reports/worker/${worker.id}`).then(r=>setReport(r.data));
    } else setReport(null);
  },[worker]);

  if(!worker) return <div className="panel"><h2>Raport Individual</h2><p>Zgjidh punëtor.</p></div>;

  return (
    <div className="panel">
      <h2>Raport Individual</h2>
      {!report && <p>Loading...</p>}
      {report && (
        <table className="mini">
          <tbody>
            <tr><td>Emri</td><td>{report.worker.first_name} {report.worker.last_name}</td></tr>
            <tr><td>Total Orë</td><td>{report.total_hours}</td></tr>
            <tr><td>€/Orë</td><td>{report.hourly_rate}</td></tr>
            <tr><td>Vlera Orëve</td><td>{report.total_due.toFixed(2)} €</td></tr>
            <tr><td>Paguar</td><td>{report.total_paid.toFixed(2)} €</td></tr>
            <tr><td>Avanca</td><td>{report.total_advances.toFixed(2)} €</td></tr>
            <tr><td><strong>Bilanci</strong></td><td><strong>{report.balance.toFixed(2)} €</strong></td></tr>
          </tbody>
        </table>
      )}
      {report && <button onClick={()=>window.open(`/api/reports/worker/${worker.id}/print`,'_blank')}>Printo</button>}
    </div>
  );
}
