import WorkerPanel from './components/WorkerPanel';
import HamburgerMenu from './components/HamburgerMenu';
import Login from './components/Login';
import PaymentsPanel from './components/PaymentsPanel';

function App() {
  const [section, setSection] = useState('Punëtorët');
  const [loggedIn, setLoggedIn] = useState(false);
  const sections = [
    'Sektorët',
    'Punëtorët',
    'Orët e Punës',
    'Pagat & Avancat',
    'Raporti Përgjithshëm',
    'Raporti Individual'
  ];

  // Custom login handler for Login component
  const handleLogin = () => {
    setLoggedIn(true);
  };

  return (
    <div>
      {!loggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <header>
            <nav style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <h1 style={{ margin: 0 }}>Enisi-MS</h1>
              <HamburgerMenu sections={sections} onSelect={setSection} />
            </nav>
          </header>
          <main>
            {section === 'Sektorët' && <SectorPanel />}
            {section === 'Punëtorët' && <WorkerPanel />}
            {section === 'Orët e Punës' && <WorkHoursPanel />}
            {section === 'Pagat & Avancat' && <PaymentsPanel />}
            {section === 'Raporti Përgjithshëm' && <ReportPanel />}
            {section === 'Raporti Individual' && <IndividualReportPanel />}
          </main>
        </>
      )}
    </div>
  );
}

export default App;
import React, { useState } from 'react';
