import React, { useState, useRef, useEffect } from 'react';

function HamburgerMenu({ sections, onSelect }) {
  const [open, setOpen] = useState(false);
  const menuRef = useRef();

  // Mbyll menunë kur klikohet jashtë saj
  useEffect(() => {
    function handleClickOutside(e) {
      if (open && menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    }
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [open]);

  return (
    <div style={{ position: 'relative', zIndex: 1001 }}>
      <button
        style={{
          background: 'transparent',
          border: 'none',
          fontSize: '2rem',
          cursor: 'pointer',
          color: '#fff',
          transition: 'color 0.2s'
        }}
        onClick={() => setOpen(!open)}
        aria-label="Menu"
      >
        &#9776;
      </button>
      {open && (
        <>
          {/* Overlay i errët pas menusë */}
          <div
            style={{
              position: 'fixed',
              top: 0, left: 0, width: '100vw', height: '100vh',
              background: 'rgba(45,55,72,0.45)',
              zIndex: 1000,
              animation: 'fadeIn 0.3s'
            }}
            onClick={() => setOpen(false)}
          />
          {/* Menuja me animacion dhe pamje të bukur */}
          <div
            ref={menuRef}
            style={{
              position: 'absolute',
              top: '3rem',
              right: '1rem',
              background: 'linear-gradient(90deg, #3182ce 0%, #38a169 100%)',
              color: '#fff',
              borderRadius: '12px',
              boxShadow: '0 6px 24px rgba(49,130,206,0.18)',
              zIndex: 1001,
              minWidth: '180px',
              animation: 'slideDown 0.25s'
            }}
          >
            <ul style={{ listStyle: 'none', margin: 0, padding: '1rem 0.5rem' }}>
              {sections.map(s => (
                <li key={s} style={{ margin: '0.5rem 0' }}>
                  <button
                    style={{
                      background: 'none',
                      border: 'none',
                      color: '#fff',
                      fontSize: '1.08rem',
                      cursor: 'pointer',
                      width: '100%',
                      textAlign: 'left',
                      padding: '0.5rem 0.7rem',
                      borderRadius: '8px',
                      transition: 'background 0.18s'
                    }}
                    onClick={() => { onSelect(s); setOpen(false); }}
                    onMouseOver={e => e.currentTarget.style.background = 'rgba(255,255,255,0.13)'}
                    onMouseOut={e => e.currentTarget.style.background = 'none'}
                  >
                    {s}
                  </button>
                </li>
              ))}
            </ul>
          </div>
          {/* Animacionet CSS */}
          <style>
            {`
              @keyframes slideDown {
                from { opacity: 0; transform: translateY(-16px);}
                to { opacity: 1; transform: translateY(0);}
              }
              @keyframes fadeIn {
                from { opacity: 0;}
                to { opacity: 1;}
              }
            `}
          </style>
        </>
      )}
    </div>
  );
}

export default HamburgerMenu;
