import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WorkerPanel.css';

function WorkerPanel({ t }) {
  const [workers, setWorkers] = useState([]);
  const [sectors, setSectors] = useState([]);
  const [form, setForm] = useState({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' });
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
    fetchSectors();
  }, []);

  const fetchSectors = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/sectors');
      setSectors(res.data);
    } catch (err) {
      setToast(t.serverError);
    }
  };

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast(t.serverError);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:4000/api/workers/${editId}`, form);
        setToast(t.workerUpdated);
      } else {
        await axios.post('http://localhost:4000/api/workers', form);
        setToast(t.workerAdded);
      }
      setForm({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' });
      setEditId(null);
      fetchWorkers();
    } catch (err) {
      setToast(t.saveError);
    }
  };

  const handleEdit = worker => {
    setForm({
      first_name: worker.first_name,
      last_name: worker.last_name,
      position: worker.position,
      hourly_rate: worker.hourly_rate,
      sector_id: worker.sector_id || ''
    });
    setEditId(worker.id);
  };

  const handleDelete = async id => {
    try {
      await axios.delete(`http://localhost:4000/api/workers/${id}`);
      setToast(t.workerDeleted);
      fetchWorkers();
    } catch (err) {
      setToast(t.deleteError);
    }
  };

  const filteredWorkers = workers.filter(w =>
    w.first_name.toLowerCase().includes(search.toLowerCase()) ||
    w.last_name.toLowerCase().includes(search.toLowerCase()) ||
    w.position.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="worker-panel">
      <h2 className="wp-title">{t.workerPanelTitle}</h2>
      <form className="wp-form" onSubmit={handleSubmit}>
        <input name="first_name" value={form.first_name} onChange={handleChange} placeholder={t.firstName} required />
        <input name="last_name" value={form.last_name} onChange={handleChange} placeholder={t.lastName} required />
        <select name="sector_id" value={form.sector_id} onChange={handleChange} required>
          <option value="">{t.selectSector}</option>
          {sectors.map(sector => (
            <option key={sector.id} value={sector.id}>{sector.name}</option>
          ))}
        </select>
        <input name="hourly_rate" value={form.hourly_rate} onChange={handleChange} placeholder={t.hourlyRate} type="number" step="0.01" required />
        <button type="submit" className="wp-btn-main">{editId ? t.saveChanges : t.addWorker}</button>
        {editId && <button type="button" className="wp-btn-cancel" onClick={() => { setEditId(null); setForm({ first_name: '', last_name: '', position: '', hourly_rate: '', sector_id: '' }); }}>{t.cancel}</button>}
      </form>
      <input
        type="text"
        className="wp-search"
        placeholder={t.search}
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="wp-table-wrapper">
        <table className="table wp-table">
          <thead>
            <tr>
              <th>{t.firstName}</th>
              <th>{t.lastName}</th>
              <th>{t.position}</th>
              <th>{t.sector}</th>
              <th>{t.hourlyRate}</th>
              <th>{t.actions}</th>
            </tr>
          </thead>
          <tbody>
            {filteredWorkers.map(worker => (
              <tr key={worker.id}>
                <td>{worker.first_name}</td>
                <td>{worker.last_name}</td>
                <td>{worker.position}</td>
                <td>{worker.sector_name || '-'}</td>
                <td>{worker.hourly_rate} €</td>
                <td>
                  <button className="wp-btn-edit" onClick={() => handleEdit(worker)}>{t.edit}</button>
                  <button className="wp-btn-delete" onClick={() => handleDelete(worker.id)}>{t.delete}</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {toast && <div className="wp-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default WorkerPanel;
