import React, { useEffect, useState } from 'react';
import { withBase } from '../api';

// Admin panel to list and manage users (pending, approve, reject)
export default function AdminApprovalPanel() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('pending');

  const load = () => {
    setLoading(true); setError(null);
    fetch(withBase(`/api/admin/users?status=${filter}`), { credentials: 'include' })
      .then(r => r.ok ? r.json() : Promise.reject(r))
      .then(setUsers)
      .catch(e => setError('Nuk u lexua lista'))
      .finally(()=> setLoading(false));
  };

  useEffect(() => { load(); }, [filter]);

  const approve = (id) => {
    fetch(withBase(`/api/admin/users/${id}/approve`), { method:'POST', credentials:'include' })
      .then(r => r.ok ? r.json() : Promise.reject(r))
      .then(()=> load())
      .catch(()=> setError('Gabim gjatë aprovimit'));
  };
  const rejectUser = (id) => {
    fetch(withBase(`/api/admin/users/${id}/reject`), { method:'POST', credentials:'include' })
      .then(r => r.ok ? r.json() : Promise.reject(r))
      .then(()=> load())
      .catch(()=> setError('Gabim gjatë refuzimit'));
  };

  return (
    <div style={{padding:'1rem 1.5rem',fontFamily:'Inter,system-ui,sans-serif'}}>
      <h3 style={{marginTop:0,marginBottom:'1rem',fontSize:'1.4rem'}}>Menaxhimi i Përdoruesve</h3>
      <div style={{display:'flex',gap:'0.5rem',marginBottom:'1rem',flexWrap:'wrap'}}>
        {['pending','approved','rejected','all'].map(f => (
          <button key={f} disabled={filter===f} onClick={()=>setFilter(f)} style={{
            background:filter===f?'#4e8ef7':'#eef2f9',
            color:filter===f?'#fff':'#334155',
            border:'none',
            padding:'0.5rem 0.9rem',
            borderRadius:'8px',
            cursor:filter===f?'default':'pointer',
            fontSize:'0.8rem',
            fontWeight:500,
            letterSpacing:'0.5px'
          }}>{f.toUpperCase()}</button>
        ))}
      </div>
      {loading && <p style={{color:'#555'}}>Duke lexuar...</p>}
      {error && <p style={{color:'red'}}>{error}</p>}
      {users.length === 0 && !loading && <p style={{color:'#666'}}>Asnjë përdorues.</p>}
      {users.length>0 && (
        <div style={{overflowX:'auto',border:'1px solid #e2e8f0',borderRadius:'12px',background:'#fff',boxShadow:'0 2px 6px rgba(0,0,0,0.05)'}}>
          <table style={{width:'100%',borderCollapse:'separate',borderSpacing:0,fontSize:'0.8rem'}}>
            <thead>
              <tr style={{background:'#f1f5f9'}}>
                {['ID','Username','Emri','Provider','Status','Roli','Krijuar','Last IP','Veprime'].map(h => <th key={h} style={{textAlign:'left',padding:'0.6rem 0.75rem',fontWeight:600,color:'#475569',fontSize:'0.7rem'}}>{h}</th>)}
              </tr>
            </thead>
            <tbody>
              {users.map(u => {
                const statusLabel = u.approved === 1 ? 'approved' : (u.approved === 0 ? 'pending' : 'rejected');
                const statusColor = statusLabel==='approved' ? '#16a34a' : statusLabel==='pending' ? '#d97706' : '#dc2626';
                return (
                  <tr key={u.id} style={{borderBottom:'1px solid #edf2f7'}}>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.id}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.username}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.display_name || '-'}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.provider || '-'}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>
                      <span style={{background:statusColor+'22',color:statusColor,padding:'0.25rem 0.55rem',borderRadius:'999px',fontWeight:500,fontSize:'0.65rem',letterSpacing:'0.5px'}}>{statusLabel.toUpperCase()}</span>
                    </td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.role}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.created_at || '-'}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>{u.last_login_ip || '-'}</td>
                    <td style={{padding:'0.55rem 0.75rem'}}>
                      <div style={{display:'flex',gap:'0.35rem'}}>
                        {u.approved === 0 && <button onClick={()=>approve(u.id)} style={{background:'#16a34a',border:'none',color:'#fff',padding:'0.35rem 0.6rem',fontSize:'0.65rem',borderRadius:'6px',cursor:'pointer'}}>Aprovo</button>}
                        {u.approved === 0 && <button onClick={()=>rejectUser(u.id)} style={{background:'#dc2626',border:'none',color:'#fff',padding:'0.35rem 0.6rem',fontSize:'0.65rem',borderRadius:'6px',cursor:'pointer'}}>Refuzo</button>}
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
      <div style={{marginTop:'0.75rem',display:'flex',gap:'0.5rem'}}>
        <button onClick={load} disabled={loading} style={{background:'#4e8ef7',color:'#fff',border:'none',padding:'0.55rem 1rem',borderRadius:'8px',cursor:'pointer',fontSize:'0.75rem',fontWeight:500}}>{loading?'Duke rifreskuar...':'Rifresko'}</button>
      </div>
    </div>
  );
}
