import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WorkerPanel.css';

function WorkerPanel() {
  const [workers, setWorkers] = useState([]);
  const [form, setForm] = useState({ first_name: '', last_name: '', position: '', hourly_rate: '' });
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editId) {
        await axios.put(`http://localhost:4000/api/workers/${editId}`, form);
        setToast('Punëtori u përditësua!');
      } else {
        await axios.post('http://localhost:4000/api/workers', form);
        setToast('Punëtori u shtua!');
      }
      setForm({ first_name: '', last_name: '', position: '', hourly_rate: '' });
      setEditId(null);
      fetchWorkers();
    } catch (err) {
      setToast('Gabim gjatë ruajtjes!');
    }
  };

  const handleEdit = worker => {
    setForm(worker);
    setEditId(worker.id);
  };

  const handleDelete = async id => {
    try {
      await axios.delete(`http://localhost:4000/api/workers/${id}`);
      setToast('Punëtori u fshi!');
      fetchWorkers();
    } catch (err) {
      setToast('Gabim gjatë fshirjes!');
    }
  };

  const filteredWorkers = workers.filter(w =>
    w.first_name.toLowerCase().includes(search.toLowerCase()) ||
    w.last_name.toLowerCase().includes(search.toLowerCase()) ||
    w.position.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="worker-panel">
      <h2 className="wp-title">Regjistrimi i Punëtorëve</h2>
      <form className="wp-form" onSubmit={handleSubmit}>
        <input name="first_name" value={form.first_name} onChange={handleChange} placeholder="Emri" required />
        <input name="last_name" value={form.last_name} onChange={handleChange} placeholder="Mbiemri" required />
        <input name="position" value={form.position} onChange={handleChange} placeholder="Pozicioni" required />
        <input name="hourly_rate" value={form.hourly_rate} onChange={handleChange} placeholder="Çmimi i orës" type="number" step="0.01" required />
        <button type="submit" className="wp-btn-main">{editId ? 'Ruaj Ndryshimet' : 'Shto Punëtorin'}</button>
        {editId && <button type="button" className="wp-btn-cancel" onClick={() => { setEditId(null); setForm({ first_name: '', last_name: '', position: '', hourly_rate: '' }); }}>Anulo</button>}
      </form>
      <input
        type="text"
        className="wp-search"
        placeholder="Kërko..."
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="wp-table-wrapper">
        <table className="table wp-table">
          <thead>
            <tr>
              <th>Emri</th>
              <th>Mbiemri</th>
              <th>Pozicioni</th>
              <th>Çmimi i Orës</th>
              <th>Veprime</th>
            </tr>
          </thead>
          <tbody>
            {filteredWorkers.map(worker => (
              <tr key={worker.id}>
                <td>{worker.first_name}</td>
                <td>{worker.last_name}</td>
                <td>{worker.position}</td>
                <td>{worker.hourly_rate} €</td>
                <td>
                  <button className="wp-btn-edit" onClick={() => handleEdit(worker)}>Edito</button>
                  <button className="wp-btn-delete" onClick={() => handleDelete(worker.id)}>Fshij</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {toast && <div className="wp-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default WorkerPanel;
