import React, { useState } from 'react';
import Login from './components/Login';
import WorkerPanel from './components/WorkerPanel';

const tabs = [
  { key:'workers', label:'Punëtorët' }
  // ...mund të shtosh orët, pagesat, raportet më vonë
];

export default function App(){
  const [auth,setAuth] = useState(true); // vendos false për të parë login
  const [active,setActive] = useState('workers');

  if(!auth) return <Login onSuccess={()=>setAuth(true)} />;

  return (
    <div style={{fontFamily:'system-ui,Arial',padding:'20px'}}>
      <h1>Enisi</h1>
      <nav style={{display:'flex',gap:8,marginBottom:20}}>
        {tabs.map(t=>(
          <button
            key={t.key}
            onClick={()=>setActive(t.key)}
            style={{
              padding:'6px 14px',
              border:'1px solid #2563eb',
              background: active===t.key ? '#2563eb' : '#ffffff',
              color: active===t.key ? '#fff':'#2563eb',
              borderRadius:6,
              cursor:'pointer'
            }}
          >{t.label}</button>
        ))}
      </nav>
      {active==='workers' && <WorkerPanel />}
    </div>
  );
}
