import React, { useState } from 'react';

function HamburgerMenu({ sections, onSelect }) {
  const [open, setOpen] = useState(false);

  return (
    <div>
      <button
        style={{ background: 'transparent', border: 'none', fontSize: '2rem', cursor: 'pointer', color: '#fff' }}
        onClick={() => setOpen(!open)}
        aria-label="Menu"
      >
        &#9776;
      </button>
      {open && (
        <div style={{ position: 'absolute', top: '3rem', right: '1rem', background: '#2d3748', color: '#fff', borderRadius: '8px', boxShadow: '0 2px 8px rgba(0,0,0,0.2)', zIndex: 1000 }}>
          <ul style={{ listStyle: 'none', margin: 0, padding: '1rem' }}>
            {sections.map(s => (
              <li key={s} style={{ margin: '0.5rem 0' }}>
                <button style={{ background: 'none', border: 'none', color: '#fff', fontSize: '1rem', cursor: 'pointer' }} onClick={() => { onSelect(s); setOpen(false); }}>
                  {s}
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default HamburgerMenu;
