import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './PaymentsPanel.css';

export default function PaymentsPanel({ worker }) {
  const [payments, setPayments] = useState([]);
  const [advances, setAdvances] = useState([]);
  const [pay, setPay] = useState({ pay_date: '', amount: '', note: '' });
  const [adv, setAdv] = useState({ advance_date: '', amount: '', note: '' });

  const load = async () => {
    if (!worker) return;
    const p = await axios.get('/api/payments', { params: { worker_id: worker.id } });
    const a = await axios.get('/api/advances', { params: { worker_id: worker.id } });
    setPayments(p.data);
    setAdvances(a.data);
  };
  useEffect(() => { load(); }, [worker]);

  const submitPay = async e => {
    e.preventDefault();
    if (!worker) return;
    await axios.post('/api/payments', { ...pay, worker_id: worker.id, amount: parseFloat(pay.amount) });
    setPay({ pay_date: '', amount: '', note: '' });
    load();
  };
  const submitAdv = async e => {
    e.preventDefault();
    if (!worker) return;
    await axios.post('/api/advances', { ...adv, worker_id: worker.id, amount: parseFloat(adv.amount) });
    setAdv({ advance_date: '', amount: '', note: '' });
    load();
  };
  const del = async (id, type) => {
    if (!window.confirm('Fshije?')) return;
    await axios.delete(`/api/${type}/${id}`);
    load();
  };

  return (
    <div className="panel">
      <h2>Pagat & Avancat {worker ? `(${worker.first_name})` : ''}</h2>
      {!worker && <p>Zgjidh një punëtor.</p>}
      {worker && <>
        <div className="two">
          <div>
            <h3>Pagë</h3>
            <form className="inline" onSubmit={submitPay}>
              <input type="date" value={pay.pay_date} onChange={e => setPay({ ...pay, pay_date: e.target.value })} />
              <input type="number" step="0.01" placeholder="Shuma" value={pay.amount} onChange={e => setPay({ ...pay, amount: e.target.value })} style={{ width: 90 }} />
              <input placeholder="Shënim" value={pay.note} onChange={e => setPay({ ...pay, note: e.target.value })} />
              <button type="submit">Shto</button>
            </form>
            <ul className="simple-list">
              {payments.map(p => <li key={p.id}>{p.pay_date} - {p.amount}€ <button onClick={() => del(p.id, 'payments')}>X</button></li>)}
              {!payments.length && <li>Ska</li>}
            </ul>
          </div>
          <div>
            <h3>Avancë</h3>
            <form className="inline" onSubmit={submitAdv}>
              <input type="date" value={adv.advance_date} onChange={e => setAdv({ ...adv, advance_date: e.target.value })} />
              <input type="number" step="0.01" placeholder="Shuma" value={adv.amount} onChange={e => setAdv({ ...adv, amount: e.target.value })} style={{ width: 90 }} />
              <input placeholder="Shënim" value={adv.note} onChange={e => setAdv({ ...adv, note: e.target.value })} />
              <button type="submit">Shto</button>
            </form>
            <ul className="simple-list">
              {advances.map(a => <li key={a.id}>{a.advance_date} - {a.amount}€ <button onClick={() => del(a.id, 'advances')}>X</button></li>)}
              {!advances.length && <li>Ska</li>}
            </ul>
          </div>
        </div>
      </>}
    </div>
  );
}
