import React, { useState, useEffect } from 'react';
import axios from 'axios';

function WorkerPanel() {
  const [workers, setWorkers] = useState([]);
  const [form, setForm] = useState({ first_name: '', last_name: '', position: '', hourly_rate: '' });
  const [editId, setEditId] = useState(null);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchWorkers();
  }, []);

  const fetchWorkers = async () => {
    const res = await axios.get('http://localhost:4000/api/workers');
    setWorkers(res.data);
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (editId) {
      await axios.put(`http://localhost:4000/api/workers/${editId}`, form);
    } else {
      await axios.post('http://localhost:4000/api/workers', form);
    }
    setForm({ first_name: '', last_name: '', position: '', hourly_rate: '' });
    setEditId(null);
    fetchWorkers();
  };

  const handleEdit = worker => {
    setForm(worker);
    setEditId(worker.id);
  };

  const handleDelete = async id => {
    await axios.delete(`http://localhost:4000/api/workers/${id}`);
    fetchWorkers();
  };

  const filteredWorkers = workers.filter(w =>
    w.first_name.toLowerCase().includes(search.toLowerCase()) ||
    w.last_name.toLowerCase().includes(search.toLowerCase()) ||
    w.position.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>Regjistrimi i Punëtorëve</h2>
      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
        <input name="first_name" value={form.first_name} onChange={handleChange} placeholder="Emri" required />
        <input name="last_name" value={form.last_name} onChange={handleChange} placeholder="Mbiemri" required />
        <input name="position" value={form.position} onChange={handleChange} placeholder="Pozicioni" required />
        <input name="hourly_rate" value={form.hourly_rate} onChange={handleChange} placeholder="Çmimi i orës" type="number" step="0.01" required />
        <button type="submit">{editId ? 'Ruaj Ndryshimet' : 'Shto Punëtorin'}</button>
        {editId && <button type="button" onClick={() => { setEditId(null); setForm({ first_name: '', last_name: '', position: '', hourly_rate: '' }); }}>Anulo</button>}
      </form>
      <input
        type="text"
        placeholder="Kërko..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ margin: '1rem 0', padding: '0.5rem', width: '100%', maxWidth: '300px' }}
      />
      <table className="table">
        <thead>
          <tr>
            <th>Emri</th>
            <th>Mbiemri</th>
            <th>Pozicioni</th>
            <th>Çmimi i Orës</th>
            <th>Veprime</th>
          </tr>
        </thead>
        <tbody>
          {filteredWorkers.map(worker => (
            <tr key={worker.id}>
              <td>{worker.first_name}</td>
              <td>{worker.last_name}</td>
              <td>{worker.position}</td>
              <td>{worker.hourly_rate} €</td>
              <td>
                <button onClick={() => handleEdit(worker)}>Edito</button>
                <button onClick={() => handleDelete(worker.id)}>Fshij</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default WorkerPanel;
