import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './IndividualReportPanel.css';
import SearchInput from './SearchInput';

function IndividualReportPanel() {
  const [workers, setWorkers] = useState([]);
  const [workerId, setWorkerId] = useState('');
  const [start, setStart] = useState('');
  const [end, setEnd] = useState(new Date().toISOString().slice(0, 10));
  const [report, setReport] = useState(null);
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const fetchReport = async () => {
    if (!workerId || !start || !end) return;
    try {
      const res = await axios.get(`http://localhost:4000/api/report/${workerId}?start=${start}&end=${end}`);
      setReport(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  // Set start date to registration date when worker is selected
  useEffect(() => {
    if (workerId) {
      const worker = workers.find(w => w.id === parseInt(workerId));
      if (worker && worker.created_at) {
        setStart(worker.created_at.slice(0, 10));
      } else {
        setStart('');
      }
    }
  }, [workerId, workers]);

  return (
    <div className="indiv-panel">
      <h2 className="indiv-title">Raport Individual për Punëtorin</h2>
      <div className="indiv-controls">
        <select value={workerId} onChange={e => setWorkerId(e.target.value)} required>
          <option value="">Zgjidh Punëtorin</option>
          {workers.map(worker => (
            <option key={worker.id} value={worker.id}>
              {worker.first_name} {worker.last_name} - {worker.position}
            </option>
          ))}
        </select>
  <SearchInput type="date" name="start" value={start} onChange={e => setStart(e.target.value)} required />
  <SearchInput type="date" name="end" value={end} onChange={e => setEnd(e.target.value)} required />
        <button className="indiv-btn-main" onClick={fetchReport}>Shfaq Raportin</button>
        {report && <button className="indiv-btn-print" onClick={() => window.print()}>Printo</button>}
      </div>
      {report && (
        <div className="indiv-table-wrapper">
          <h3>{report.worker.first_name} {report.worker.last_name} - {report.worker.position}</h3>
          <table className="table indiv-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Orët</th>
                <th>Paga</th>
                <th>Avancat</th>
              </tr>
            </thead>
            <tbody>
              {report.workhours.map(w => (
                <tr key={w.id}>
                  <td>{w.date}</td>
                  <td>{w.hours}</td>
                  <td></td>
                  <td></td>
                </tr>
              ))}
              {report.payments.map(p => (
                <tr key={p.id}>
                  <td>{p.date}</td>
                  <td></td>
                  <td>{p.type === 'paga' ? p.amount + ' €' : ''}</td>
                  <td>{p.type === 'avanc' ? p.amount + ' €' : ''}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ marginTop: '1rem' }}>
            <strong>Totali Orëve:</strong> {report.workhours.reduce((sum, w) => sum + w.hours, 0)}<br />
            <strong>Totali Pagave:</strong> {report.payments.filter(p => p.type === 'paga').reduce((sum, p) => sum + p.amount, 0)} €<br />
            <strong>Totali Avancave:</strong> {report.payments.filter(p => p.type === 'avanc').reduce((sum, p) => sum + p.amount, 0)} €<br />
            <strong>Totali i pritshëm (orë * çmimi):</strong> {report.workhours.reduce((sum, w) => sum + w.hours, 0) * report.worker.hourly_rate} €<br />
            <strong>Balanca:</strong> {
              (() => {
                const totalPaid = report.payments.reduce((sum, p) => sum + p.amount, 0);
                const expected = report.workhours.reduce((sum, w) => sum + w.hours, 0) * report.worker.hourly_rate;
                const balance = totalPaid - expected;
                if (balance < 0) {
                  return '-' + Math.abs(balance).toFixed(2) + ' €';
                } else {
                  return balance.toFixed(2) + ' €';
                }
              })()
            }
          </div>
        </div>
      )}
      {toast && <div className="indiv-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default IndividualReportPanel;
