import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PaymentsPanel.css';

function PaymentsPanel({ t }) {
  const [workers, setWorkers] = useState([]);
  const [payments, setPayments] = useState([]);
  const today = new Date().toISOString().slice(0, 10);
  const [form, setForm] = useState({ worker_id: '', date: today, amount: '', type: 'paga' });
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
    fetchPayments();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast(t.serverError);
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/payments');
      setPayments(res.data);
    } catch (err) {
      setToast(t.serverError);
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (form.id) {
        await axios.put(`http://localhost:4000/api/payments/${form.id}`, form);
        setToast(t.paymentUpdated);
      } else {
        await axios.post('http://localhost:4000/api/payments', form);
        setToast(t.paymentAdded);
      }
      setForm({ worker_id: '', date: today, amount: '', type: 'paga' });
      fetchPayments();
    } catch (err) {
      setToast(t.saveError);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm(t.confirmDeletePayment)) {
      try {
        await axios.delete(`http://localhost:4000/api/payments/${id}`);
        setToast(t.paymentDeleted);
        fetchPayments();
      } catch (err) {
        setToast(t.deleteError);
      }
    }
  };

  const filteredPayments = payments.filter(p => {
    const worker = workers.find(x => x.id === p.worker_id);
    return (
      (!search || (worker && (worker.first_name + ' ' + worker.last_name).toLowerCase().includes(search.toLowerCase())))
    );
  });

  return (
    <div className="pay-panel">
      <h2 className="pay-title">{t.paymentsPanelTitle}</h2>
      <form className="pay-form" onSubmit={handleSubmit}>
        <select name="worker_id" value={form.worker_id} onChange={handleChange} required>
          <option value="">{t.selectWorker}</option>
          {workers.map(worker => (
            <option key={worker.id} value={worker.id}>
              {worker.first_name} {worker.last_name} - {worker.position}
            </option>
          ))}
        </select>
        <input name="date" value={form.date} onChange={handleChange} type="date" required />
        <input name="amount" value={form.amount} onChange={handleChange} placeholder={t.amount} type="number" step="0.01" required />
        <select name="type" value={form.type} onChange={handleChange} required>
          <option value="paga">{t.salary}</option>
          <option value="avanc">{t.advance}</option>
        </select>
        <button type="submit" className="pay-btn-main">{form.id ? t.saveChanges : t.addPayment}</button>
        {form.id && <button type="button" className="pay-btn-cancel" onClick={() => setForm({ worker_id: '', date: today, amount: '', type: 'paga' })}>{t.cancel}</button>}
      </form>
      <input
        type="text"
        className="pay-search"
        placeholder={t.searchWorker}
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="pay-table-wrapper">
        <table className="table pay-table">
          <thead>
            <tr>
              <th>{t.worker}</th>
              <th>{t.date}</th>
              <th>{t.amount}</th>
              <th>{t.type}</th>
              <th>{t.actions}</th>
            </tr>
          </thead>
          <tbody>
            {filteredPayments.map(p => {
              const worker = workers.find(x => x.id === p.worker_id);
              return (
                <tr key={p.id}>
                  <td>{worker ? `${worker.first_name} ${worker.last_name}` : '-'}</td>
                  <td>{p.date}</td>
                  <td>{p.amount} €</td>
                  <td>{p.type === 'paga' ? t.salary : t.advance}</td>
                  <td>
                    <button className="pay-btn-edit" type="button" onClick={() => setForm({ worker_id: p.worker_id, date: p.date, amount: p.amount, type: p.type, id: p.id })}>{t.edit}</button>
                    <button className="pay-btn-delete" type="button" onClick={() => handleDelete(p.id)}>{t.delete}</button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {toast && <div className="pay-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default PaymentsPanel;
