import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import './WorkerPanel.css';

export default function WorkerPanel({ onSelect, selectedWorker }) {
  const empty = { first_name: '', last_name: '', position: '', hourly_rate: '' };
  const [workers, setWorkers] = useState([]);
  const [form, setForm] = useState(empty);
  const [editingId, setEditingId] = useState(null);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState(null);

  const load = async () => {
    setLoading(true);
    try {
      const { data } = await axios.get('/api/workers');
      setWorkers(data);
      if (selectedWorker) {
        const still = data.find(w => w.id === selectedWorker.id);
        if (!still) onSelect?.(null);
      }
    } catch (e) {
      setMsg('Gabim në marrje të dhënave');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); /* eslint-disable-next-line */ }, []);

  const change = (k, v) => setForm(f => ({ ...f, [k]: v }));

  const submit = async e => {
    e.preventDefault();
    if (!form.first_name.trim() || !form.last_name.trim()) return;
    const payload = {
      first_name: form.first_name.trim(),
      last_name: form.last_name.trim(),
      position: form.position.trim(),
      hourly_rate: parseFloat(form.hourly_rate || 0) || 0
    };
    try {
      if (editingId) {
        await axios.put(`/api/workers/${editingId}`, payload);
        setMsg('U përditësua');
      } else {
        await axios.post('/api/workers', payload);
        setMsg('U shtua');
      }
      cancel();
      load();
    } catch (e) {
      setMsg('Gabim në ruajtje');
    }
  };

  const edit = w => {
    setEditingId(w.id);
    setForm({
      first_name: w.first_name,
      last_name: w.last_name,
      position: w.position || '',
      hourly_rate: w.hourly_rate
    });
  };

  const remove = async (w) => {
    if (!window.confirm(`Fshij ${w.first_name}?`)) return;
    try {
      await axios.delete(`/api/workers/${w.id}`);
      if (selectedWorker?.id === w.id) onSelect?.(null);
      setMsg('U fshi');
      load();
    } catch {
      setMsg('Gabim në fshirje');
    }
  };

  const cancel = () => {
    setEditingId(null);
    setForm(empty);
  };

  const filtered = useMemo(() => {
    if (!search.trim()) return workers;
    const q = search.toLowerCase();
    return workers.filter(w =>
      [w.first_name, w.last_name, w.position]
        .filter(Boolean)
        .some(x => x.toLowerCase().includes(q))
    );
  }, [workers, search]);

  return (
    <div className="wp-panel">
      <div className="wp-head">
        <h2>Punëtorët</h2>
        <div className="wp-actions">
          <input
            className="wp-search"
            placeholder="Kërko..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
        </div>
      </div>

      <form className="wp-form" onSubmit={submit}>
        <input
          placeholder="Emri"
          value={form.first_name}
          onChange={e => change('first_name', e.target.value)}
          required
        />
        <input
          placeholder="Mbiemri"
          value={form.last_name}
          onChange={e => change('last_name', e.target.value)}
          required
        />
        <input
          placeholder="Pozita"
            value={form.position}
          onChange={e => change('position', e.target.value)}
        />
        <input
          placeholder="€/Orë"
          type="number"
          step="0.01"
          value={form.hourly_rate}
          onChange={e => change('hourly_rate', e.target.value)}
          required
          min="0"
        />
        <button type="submit" className="wp-btn-main">
          {editingId ? 'Ruaj' : 'Shto'}
        </button>
        {editingId && (
          <button type="button" className="wp-btn-alt" onClick={cancel}>
            Anulo
          </button>
        )}
      </form>

      {msg && <div className="wp-msg" onClick={() => setMsg(null)}>{msg}</div>}

      <div className="wp-table-wrapper">
        <table className="wp-table">
          <thead>
            <tr>
              <th>Emri</th>
              <th>Pozita</th>
              <th>€/Orë</th>
              <th style={{ width: 150 }}>Veprime</th>
            </tr>
          </thead>
          <tbody>
            {loading && (
              <tr><td colSpan="4">Duke u ngarkuar...</td></tr>
            )}
            {!loading && !filtered.length && (
              <tr><td colSpan="4">Ska të dhëna</td></tr>
            )}
            {!loading && filtered.map(w => (
              <tr
                key={w.id}
                className={selectedWorker?.id === w.id ? 'wp-selected' : ''}
                onClick={() => onSelect?.(w)}
              >
                <td>{w.first_name} {w.last_name}</td>
                <td>{w.position || '-'}</td>
                <td>{w.hourly_rate}</td>
                <td className="wp-actions-cell">
                  <button
                    type="button"
                    className="wp-mini"
                    onClick={e => { e.stopPropagation(); edit(w); }}
                  >Edit</button>
                  <button
                    type="button"
                    className="wp-mini danger"
                    onClick={e => { e.stopPropagation(); remove(w); }}
                  >Fshi</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {selectedWorker && (
        <div className="wp-selected-banner">
          Zgjedhur: {selectedWorker.first_name} {selectedWorker.last_name}
        </div>
      )}
    </div>
  );
}
