import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PaymentsPanel.css';


<form className="pay-form" onSubmit={async e => {
  e.preventDefault();
  if (form.id) {
    await axios.put(`http://localhost:4000/api/payments/${form.id}`, form);
    setToast('Pagesa/Avanci u përditësua!');
  } else {
    await axios.post('http://localhost:4000/api/payments', form);
    setToast('Pagesa/Avanci u regjistrua!');
  }
  setForm({ worker_id: '', date: today, amount: '', type: 'paga' });
  fetchPayments();
}}>
  <select name="worker_id" value={form.worker_id} onChange={handleChange} required>
    <option value="">Zgjidh Punëtorin</option>
    {workers.map(worker => (
      <option key={worker.id} value={worker.id}>
        {worker.first_name} {worker.last_name} - {worker.position}
      </option>
    ))}
  </select>
  <input name="date" value={form.date} onChange={handleChange} type="date" required />
  <input name="amount" value={form.amount} onChange={handleChange} placeholder="Shuma" type="number" step="0.01" required />
  <select name="type" value={form.type} onChange={handleChange} required>
    <option value="paga">Paga</option>
    <option value="avanc">Avanc</option>
  </select>
  <button type="submit" className="pay-btn-main">{form.id ? 'Ruaj Ndryshimet' : 'Shto'}</button>
  {form.id && <button type="button" className="pay-btn-cancel" onClick={() => setForm({ worker_id: '', date: today, amount: '', type: 'paga' })}>Anulo</button>}
</form>

function PaymentsPanel() {
  const [workers, setWorkers] = useState([]);
  const [payments, setPayments] = useState([]);
  const today = new Date().toISOString().slice(0, 10);
  const [form, setForm] = useState({ worker_id: '', date: today, amount: '', type: 'paga' });
  const [search, setSearch] = useState('');
  const [toast, setToast] = useState(null);

  useEffect(() => {
    fetchWorkers();
    fetchPayments();
  }, []);

  const fetchWorkers = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/workers');
      setWorkers(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const fetchPayments = async () => {
    try {
      const res = await axios.get('http://localhost:4000/api/payments');
      setPayments(res.data);
    } catch (err) {
      setToast('Gabim në lidhje me serverin!');
    }
  };

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:4000/api/payments', form);
      setToast('Pagesa/Avanci u regjistrua!');
      setForm({ worker_id: '', date: today, amount: '', type: 'paga' });
      fetchPayments();
    } catch (err) {
      setToast('Gabim gjatë ruajtjes!');
    }
  };

  const filteredPayments = payments.filter(p => {
    const worker = workers.find(x => x.id === p.worker_id);
    return (
      (!search || (worker && (worker.first_name + ' ' + worker.last_name).toLowerCase().includes(search.toLowerCase())))
    );
  });

  return (
    <div className="pay-panel">
      <h2 className="pay-title">Regjistrimi i Pagave dhe Avancave</h2>
      <form className="pay-form" onSubmit={handleSubmit}>
        <select name="worker_id" value={form.worker_id} onChange={handleChange} required>
          <option value="">Zgjidh Punëtorin</option>
          {workers.map(worker => (
            <option key={worker.id} value={worker.id}>
              {worker.first_name} {worker.last_name} - {worker.position}
            </option>
          ))}
        </select>
        <input name="date" value={form.date} onChange={handleChange} type="date" required />
        <input name="amount" value={form.amount} onChange={handleChange} placeholder="Shuma" type="number" step="0.01" required />
        <select name="type" value={form.type} onChange={handleChange} required>
          <option value="paga">Paga</option>
          <option value="avanc">Avanc</option>
        </select>
        <button type="submit" className="pay-btn-main">Shto</button>
      </form>
      <input
        type="text"
        className="pay-search"
        placeholder="Kërko punëtorin..."
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="pay-table-wrapper">
        <table className="table pay-table">
   <thead>
  <tr>
    <th>Punëtori</th>
    <th>Data</th>
    <th>Shuma</th>
    <th>Lloji</th>
    <th>Veprime</th>
  </tr>
</thead>
<tbody>
  {filteredPayments.map(p => {
    const worker = workers.find(x => x.id === p.worker_id);
    return (
      <tr key={p.id}>
        <td>{worker ? `${worker.first_name} ${worker.last_name}` : '-'}</td>
        <td>{p.date}</td>
        <td>{p.amount} €</td>
        <td>{p.type === 'paga' ? 'Paga' : 'Avanc'}</td>
        <td>
          <button className="pay-btn-edit" onClick={() => setForm({ worker_id: p.worker_id, date: p.date, amount: p.amount, type: p.type, id: p.id })}>Edito</button>
          <button className="pay-btn-delete" onClick={async () => { await axios.delete(`http://localhost:4000/api/payments/${p.id}`); fetchPayments(); }}>Fshij</button>
        </td>
      </tr>
    );
  })}
</tbody>
        </table>
      </div>
      {toast && <div className="pay-toast" onClick={() => setToast(null)}>{toast}</div>}
    </div>
  );
}

export default PaymentsPanel;
