import React, { useState, useEffect } from 'react';
import { withBase } from './api';
import WorkerPanel from './components/WorkerPanel';
import HamburgerMenu from './components/HamburgerMenu';
import Login from './components/Login';
import SectorPanel from './components/SectorPanel';
import WorkHoursPanel from './components/WorkHoursPanel';
import PaymentsPanel from './components/PaymentsPanel';
import ReportPanel from './components/ReportPanel';
import IndividualReportPanel from './components/IndividualReportPanel';
import AdminApprovalPanel from './components/AdminApprovalPanel'; // added

function App() {
  const [section, setSection] = useState('Punëtorët');
  const [loggedIn, setLoggedIn] = useState(process.env.NODE_ENV === 'development');
  const [pending, setPending] = useState(false); // new state for approval
  const [checking, setChecking] = useState(false);
  const baseSections = [
    'Sektorët',
    'Punëtorët',
    'Orët e Punës',
    'Pagat & Avancat',
    'Raporti Përgjithshëm',
    'Raporti Individual'
  ];
  const sections = process.env.NODE_ENV === 'development' ? [...baseSections, 'Miratimi Admin'] : baseSections;

  // Custom login handler for Login component
  const handleLogin = () => {
    setLoggedIn(true);
  };

  // central session check
  const checkSession = (fromGoogle = false) => {
    setChecking(true);
    fetch(withBase('/api/me'), { credentials: 'include' })
      .then(async r => {
        if (r.ok) {
          await r.json();
          setLoggedIn(true);
          setPending(false);
        } else if (r.status === 403) {
          // pending or forbidden
          return r.json().then(j => {
            if (j && j.pending) {
              setPending(true);
              setLoggedIn(false);
            }
          });
        } else {
          setLoggedIn(false);
        }
      })
      .catch(() => {})
      .finally(() => setChecking(false));
  };

  // On mount: if returned from Google (query ?google=1) or existing session, try /api/me
  useEffect(() => {
    const url = new URL(window.location.href);
    const hasGoogle = url.searchParams.get('google');
    const isPendingParam = url.searchParams.get('pending');
    if (isPendingParam) {
      setPending(true);
      url.searchParams.delete('pending');
      window.history.replaceState({}, document.title, url.pathname + url.search);
    }
    if (!loggedIn && (hasGoogle !== null || isPendingParam)) {
      checkSession(true);
      url.searchParams.delete('google');
      window.history.replaceState({}, document.title, url.pathname + url.search);
    } else if (!loggedIn && !pending) {
      checkSession();
    }
  }, [loggedIn, pending]);

  // poll every 10s if pending to auto-enter once approved
  useEffect(() => {
    if (pending) {
      const id = setInterval(() => checkSession(), 10000);
      return () => clearInterval(id);
    }
  }, [pending]);

  if (pending) {
    return (
      <div style={{minHeight:'100vh',display:'flex',alignItems:'center',justifyContent:'center',background:'#f5f7fb',fontFamily:'Inter,system-ui,sans-serif',padding:'1rem'}}>
        <div style={{background:'#fff',borderRadius:'16px',padding:'2.5rem 2rem',maxWidth:'480px',width:'100%',boxShadow:'0 4px 12px rgba(0,0,0,0.08)',position:'relative'}}>
          <div style={{position:'absolute',top:'-24px',left:'50%',transform:'translateX(-50%)',background:'linear-gradient(135deg,#4e8ef7,#6a6ff7)',color:'#fff',padding:'0.5rem 1.25rem',borderRadius:'999px',fontSize:'0.85rem',letterSpacing:'0.5px',boxShadow:'0 4px 10px rgba(0,0,0,0.15)'}}>PENDING</div>
          <h1 style={{margin:'0 0 1rem',fontSize:'1.6rem',lineHeight:1.2,color:'#1f2d3d',textAlign:'center'}}>Llogaria juaj është në pritje</h1>
          <p style={{margin:'0 0 1rem',fontSize:'0.95rem',color:'#4a5568',textAlign:'center'}}>Administratori duhet t'ju aprovojë përpara se të vazhdoni. Ky ekran kontrollon automatikisht çdo 10 sekonda.</p>
          <div style={{display:'flex',flexDirection:'column',gap:'0.75rem',marginTop:'1.25rem'}}>
            <button onClick={() => checkSession()} disabled={checking} style={{background:'#4e8ef7',color:'#fff',border:'none',padding:'0.75rem 1.25rem',borderRadius:'10px',cursor:'pointer',fontSize:'0.95rem',fontWeight:500,display:'flex',alignItems:'center',justifyContent:'center',gap:'0.5rem',transition:'background .2s',opacity:checking?0.7:1}}>
              {checking && <span className="spinner" style={{width:16,height:16,border:'3px solid #ffffff55',borderTopColor:'#fff',borderRadius:'50%',display:'inline-block',animation:'spin 0.8s linear infinite'}}></span>}
              Rifresko tani
            </button>
            <div style={{fontSize:'0.75rem',color:'#718096',textAlign:'center'}}>Nëse kjo zgjat shumë, kontakto administratorin.</div>
          </div>
          <style>{`@keyframes spin{from{transform:rotate(0deg)}to{transform:rotate(360deg)}}`}</style>
        </div>
      </div>
    );
  }

  return (
    <div>
      {!loggedIn ? (
        <Login onLogin={handleLogin} />
      ) : (
        <>
          <header>
            <nav style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
              <h1 style={{ margin: 0 }}>Enisi-MS</h1>
              <HamburgerMenu sections={sections} onSelect={setSection} />
              {process.env.NODE_ENV === 'development' && (
                <button style={{marginLeft:'1rem'}} onClick={()=>setLoggedIn(false)}>Dil (Test)</button>
              )}
            </nav>
          </header>
          <main>
            {section === 'Sektorët' && <SectorPanel />}
            {section === 'Punëtorët' && <WorkerPanel />}
            {section === 'Orët e Punës' && <WorkHoursPanel />}
            {section === 'Pagat & Avancat' && <PaymentsPanel />}
            {section === 'Raporti Përgjithshëm' && <ReportPanel />}
            {section === 'Raporti Individual' && <IndividualReportPanel />}
            {process.env.NODE_ENV === 'development' && section === 'Miratimi Admin' && <AdminApprovalPanel />} {/* dev only */}
          </main>
        </>
      )}
    </div>
  );
}

export default App;
