import React, { useState } from 'react';

function Login({ onLogin }) {
  const [codeSent, setCodeSent] = useState(false);
  const [regCode, setRegCode] = useState('');
  // Register form submit handler
  const handleRegister = async (e) => {
    e.preventDefault();
    setRegError(null);
    setRegSuccess(false);
    setCodeSent(false);
    if (regUsername === regPassword) {
      setRegError('Username and password cannot be the same');
      return;
    }
    try {
      // Replace with your backend register API
      // Example:
      setRegSuccess(true);
      setRegUsername('');
      setRegPassword('');
      setRegCode('');
    } catch (err) {
      setRegError('Registration failed');
    }
  };
  const [forgotUsername, setForgotUsername] = useState('');
  const [forgotCode, setForgotCode] = useState('');
  // Send code automatically when forgot password is opened or resend is clicked
  const sendForgotCode = async () => {
    setResendLoading(true);
    setForgotError(null);
    try {
      // Replace with your backend code sending API
      // await axios.post(`${apiUrl}/api/send-reset-code`, { username: forgotUsername });
      setForgotCodeSent(true);
      setForgotStep(1);
    } catch (err) {
      setForgotError('Failed to send code');
    }
    setResendLoading(false);
  };
  // Removed duplicate declarations for forgotUsername and forgotCode
  const [showRegister, setShowRegister] = useState(false);
  const [showForgot, setShowForgot] = useState(false);
  const [forgotStep, setForgotStep] = useState(0); // 0: send code, 1: enter code, 2: enter username, 3: enter new password
  const [forgotCodeSent, setForgotCodeSent] = useState(false);
  // Removed duplicate declarations for forgotCode and forgotUsername
  const [forgotPassword, setForgotPassword] = useState('');
  // Removed duplicate declaration for forgotError/setForgotError
  const [forgotSuccess, setForgotSuccess] = useState(false);
  const [resendLoading, setResendLoading] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [regUsername, setRegUsername] = useState('');
  const [regPassword, setRegPassword] = useState('');
  // regCode/setRegCode already declared above, remove duplicate
  const [regError, setRegError] = useState(null);
  const [regSuccess, setRegSuccess] = useState(false);
  const [forgotError, setForgotError] = useState(null);
  // Load environment variables
  const apiUrl = process.env.REACT_APP_API_URL;
  const emailService = process.env.REACT_APP_EMAIL_SERVICE;
  const emailUser = process.env.REACT_APP_EMAIL_USER;
  const emailPass = process.env.REACT_APP_EMAIL_PASS;

  // You can use apiUrl for API calls, and emailService/emailUser/emailPass for config if needed
  // Example usage:
  // fetch(`${apiUrl}/api/login`, { ... })
  // console.log('Email config:', emailService, emailUser, emailPass);

  // Login form submit handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(false);
    try {
      // Replace with your backend login API
      // Example:
      // const res = await axios.post('http://localhost:4000/api/login', { username, password });
      setSuccess(true);
      if (onLogin) onLogin();
    } catch (err) {
      setError('Login failed');
    }
  };

  // Validate code
  const handleCodeSubmit = async (e) => {
    e.preventDefault();
    setForgotError(null);
    try {
      // Replace with your backend code validation API
      // const valid = await axios.post(`${apiUrl}/api/validate-reset-code`, { code: forgotCode });
      const valid = true; // Simulate success
      if (valid) {
        setForgotStep(2);
      } else {
        setForgotError('Invalid code');
      }
    } catch (err) {
      setForgotError('Error validating code');
    }
  };

  // Validate username
  const handleUsernameSubmit = async (e) => {
    e.preventDefault();
    setForgotError(null);
    try {
      // Replace with your backend username validation API
      // const valid = await axios.post(`${apiUrl}/api/validate-username`, { username: forgotUsername });
      const valid = true; // Simulate success
      if (valid) {
        setForgotStep(3);
      } else {
        setForgotError('Invalid username');
      }
    } catch (err) {
      setForgotError('Error validating username');
    }
  };

  // Change password
  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setForgotError(null);
    try {
      // Replace with your backend password change API
      // await axios.post(`${apiUrl}/api/change-password`, { username: forgotUsername, password: forgotPassword });
      setForgotSuccess(true);
      setShowForgot(false);
      setForgotStep(0);
      setForgotCode('');
      setForgotUsername('');
      setForgotPassword('');
    } catch (err) {
      setForgotError('Error changing password');
    }
  };

  return (
    <div className="container">
      {showForgot ? (
        <div>
          {/* Step 1: Send code automatically */}
          {forgotStep === 0 && (
            <>
              <div style={{ textAlign: 'center', marginBottom: '10px' }}>Sending code...</div>
              {sendForgotCode()}
            </>
          )}
          {/* Step 2: Enter code */}
          {forgotStep === 1 && (
            <form className="form" onSubmit={handleCodeSubmit}>
              <input required className="input" type="text" name="forgotCode" id="forgotCode" placeholder="Enter code" value={forgotCode} onChange={e => setForgotCode(e.target.value)} />
              <button type="button" className="login-button" disabled={resendLoading} onClick={sendForgotCode} style={{ marginBottom: '10px' }}>Resend Code</button>
              <input className="login-button" type="submit" value="Verify Code" />
              {forgotError && <div style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{forgotError}</div>}
            </form>
          )}
          {/* Step 3: Enter username */}
          {forgotStep === 2 && (
            <form className="form" onSubmit={handleUsernameSubmit}>
              <input required className="input" type="text" name="forgotUsername" id="forgotUsername" placeholder="Enter your username" value={forgotUsername} onChange={e => setForgotUsername(e.target.value)} />
              <input className="login-button" type="submit" value="Verify Username" />
              {forgotError && <div style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{forgotError}</div>}
            </form>
          )}
          {/* Step 4: Enter new password */}
          {forgotStep === 3 && (
            <form className="form" onSubmit={handlePasswordSubmit}>
              <input required className="input" type="password" name="forgotPassword" id="forgotPassword" placeholder="Enter new password" value={forgotPassword} onChange={e => setForgotPassword(e.target.value)} />
              <input className="login-button" type="submit" value="Save New Password" />
              {forgotError && <div style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{forgotError}</div>}
            </form>
          )}
          <button style={{ margin: '10px auto', display: 'block', background: 'none', border: 'none', color: '#0099ff', cursor: 'pointer', fontSize: '14px' }} onClick={() => { setShowForgot(false); setForgotStep(0); }}>
            Back to Login
          </button>
        </div>
      ) : showRegister ? (
        <div>
          <form className="form" onSubmit={handleRegister}>
            <input required className="input" type="text" name="regUsername" id="regUsername" placeholder="Username" value={regUsername} onChange={e => setRegUsername(e.target.value)} />
            <input required className="input" type="password" name="regPassword" id="regPassword" placeholder="Password" value={regPassword} onChange={e => setRegPassword(e.target.value)} />
            <input required className="input" type="text" name="regCode" id="regCode" placeholder="Enter code" value={regCode} onChange={e => setRegCode(e.target.value)} />
            <input className="login-button" type="submit" value="Create Account" />
          </form>
          {regError && <div style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{regError}</div>}
          {regSuccess && <div style={{ color: 'green', textAlign: 'center', marginTop: '10px' }}>Account created! You can now log in.</div>}
          <button style={{ margin: '10px auto', display: 'block', background: 'none', border: 'none', color: '#0099ff', cursor: 'pointer', fontSize: '14px' }} onClick={() => setShowRegister(false)}>
            Back to Login
          </button>
        </div>
      ) : (
        <>
          <form className="form" onSubmit={handleSubmit}>
            <input required className="input" type="text" name="username" id="username" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} />
            <input required className="input" type="password" name="password" id="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
            <span className="forgot-password"><a href="#" onClick={() => setShowForgot(true)}>Forgot Password ?</a></span>
            <input className="login-button" type="submit" value="Sign In" />
          </form>
          {error && <div style={{ color: 'red', textAlign: 'center', marginTop: '10px' }}>{error}</div>}
          {success && <div style={{ color: 'green', textAlign: 'center', marginTop: '10px' }}>Login successful!</div>}
          <button style={{ margin: '10px auto', display: 'block', background: 'none', border: 'none', color: '#0099ff', cursor: 'pointer', fontSize: '14px' }} onClick={() => setShowRegister(true)}>
            Create Account
          </button>
        </>
      )}
      <div className="social-account-container">
        <span className="title">Or Sign in with</span>
        <div className="social-accounts">
          <button className="social-button google" type="button">
            <svg className="svg" xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 488 512">
              <path d="M488 261.8C488 403.3 391.1 504 248 504 110.8 504 0 393.2 0 256S110.8 8 248 8c66.8 0 123 24.5 166.3 64.9l-67.5 64.9C258.5 52.6 94.3 116.6 94.3 256c0 86.5 69.1 156.6 153.7 156.6 98.2 0 135-70.4 140.8-106.9H248v-85.3h236.1c2.3 12.7 3.9 24.9 3.9 41.4z"></path>
            </svg>
          </button>
          <button className="social-button apple" type="button">
            <svg className="svg" xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 384 512">
              <path d="M318.7 268.7c-.2-36.7 16.4-64.4 50-84.8-18.8-26.9-47.2-41.7-84.7-44.6-35.5-2.8-74.3 20.7-88.5 20.7-15 0-49.4-19.7-76.4-19.7C63.3 141.2 4 184.8 4 273.5q0 39.3 14.4 81.2c12.8 36.7 59 126.7 107.2 125.2 25.2-.6 43-17.9 75.8-17.9 31.8 0 48.3 17.9 76.4 17.9 48.6-.7 90.4-82.5 102.6-119.3-65.2-30.7-61.7-90-61.7-91.9zm-56.6-164.2c27.3-32.4 24.8-61.9 24-72.5-24.1 1.4-52 16.4-67.9 34.9-17.5 19.8-27.8 44.3-25.6 71.9 26.1 2 49.9-11.4 69.5-34.3z"></path>
            </svg>
          </button>
          <button className="social-button twitter" type="button">
            <svg className="svg" xmlns="http://www.w3.org/2000/svg" height="1em" viewBox="0 0 512 512">
              <path d="M389.2 48h70.6L305.6 224.2 487 464H345L233.7 318.6 106.5 464H35.8L200.7 275.5 26.8 48H172.4L272.9 180.9 389.2 48zM364.4 421.8h39.1L151.1 88h-42L364.4 421.8z"></path>
            </svg>
          </button>
        </div>
      </div>
      <span className="agreement"><a href="#">Learn user licence agreement</a></span>
    </div>
  );
}

export default Login;
